import java.util.ArrayList;import java.util.HashSet;import java.util.List;import java.util.Set;public class HW7_Teamwork_27{        public static int getUniqueWordsAmount( String text ){        if( text==null||text.isEmpty( ) ){                        return 0;        }        Set<String> uniqueWords=new HashSet<>( );        for( String word: text.split( " " ) ){            uniqueWords.add( word );        }                return uniqueWords.size( );    }        public static String getTrWithRequiredLanguage( String languageName ){                List<String> languageInformation=new ArrayList<>( );                languageInformation.add( "Joy inforichland 07/05/09 0" );        for( String language: languageInformation ){                        String name=language.split( " " )[ 0 ];            if( name.equals( languageName ) ){                return language;            }                    }        return null;            }}