public class TeamWork06{        public static int getWordsAmount( String text ){        return text.split( " " ).length;    }        public static int countWords( String sentence ){        if( sentence==null||sentence.isEmpty( ) ){                        return 0;        }                String[] words=sentence.trim( ).split( " " );                return words.length;    }        public static void main( String[] args ){                String sentence="Иногда хватает мгновения, чтобы забыть жизнь, а иногда не хватает жизни, чтобы забыть мгновение.";        int wordCount=countWords( sentence );        System.out.println( wordCount );                StringBuffer sb=new StringBuffer( "Hello" );        sb.reverse( );        System.out.println( sb );                    }}